export function useDropzone(options = {}) {
  const { onDrop, accept } = options;
  const handle = (e) => {
    e.preventDefault();
    const files = Array.from(e.target.files || e.dataTransfer.files || []);
    if (onDrop) onDrop(files);
  };
  const rootProps = {
    onDragOver: (e) => e.preventDefault(),
    onDrop: handle
  };
  const inputProps = { type: 'file', accept, onChange: handle };
  return { getRootProps: () => rootProps, getInputProps: () => inputProps, isDragActive: false };
}
